                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 ;          end;
if nargin<4  || isempty(K),              K               = pi/30;      end;
if nargin<5  || isempty(S),              S               = 7;          end;

%% WLD, differential excitation
I=double(I);
diffexim=FISM_SSGSM_pro(I);

%% LPQ
LPQfilters=createLPQfilters(lpq_size);
charori= highpassmonogenic(I,2,3,'true');
charOri= (charori.*charOrientation(I).*exp(charori)).^0.333;
lpqim_Q=ri_lpq(I, LPQfilters, charOri, 'im');
M = 256;
%adjcontrast(I,0.6,1) 2.5 for Biometrika
%adjcontrast(I,10,1) and I,5,1,'false' 2.4 for Digital Persona


%% LPQ and WLD alignment
edge_size = (size(diffexim)-size(lpqim_Q))/2;
assert(isequal(edge_size,round(edge_size)));
if edge_size(1)>0,
    diffexim = diffexim(edge_size(1)+1:end-edge_size(1),:);
elseif edge_size(1)<0,
    edge_size(1)=-edge_size(1);
    lpqim_Q = lpqim_Q(edge_size(1)+1:end-edge_size(1),:);
end;
if edge_size(2)>0,
    diffexim = diffexim(:,edge_size(2)+1:end-edge_size(2));
elseif  edge_size(2)<0,
    edge_size(2)=-edge_size(2);
    lpqim_Q = lpqim_Q(:,edge_size(2)+1:end-edge_size(2));
end;


%% WLD quantization into S levels
delta = (pi-2*K) / (S-1);

th = K:delta:pi/2;
th = sort([-th,th]);
N = length(th);
assert(N-1==S);

th(1)   = th(1)   *1.1;
th(end) = th(end) *1.1;

diffexim_Q = uint8(zeros(size(diffexim)));
for i=1:S,
    diffexim_Q(  (diffexim > th(i)) & (diffexim < th(i+1))  ) = i-1;
end;


%% Masking
lpqim_Q = double(lpqim_Q);
diffexim_Q = double(diffexim_Q);
if nargin>1 && not(isempty(mask)),
    dsz = (size(mask)-size(diffexim_Q))/2;
    mask = mask(dsz(1)+1:end-dsz(1),dsz(2)+1:end-dsz(2));
    mask = not(mask);
    lpqim_Q(mask)    = NaN;
    diffexim_Q(mask) = NaN;
end;

%% 2D HISTOGRAM
% Each row is a different value of the WLD differential excitation quantized into S levels
% Each row is a different value of LPQ quantized into M levels
H2 = zeros(S,M);

mask_de = false(size(diffexim_Q,1),size(diffexim_Q,2),S);
mask_lpq = false(size(diffexim_Q,1),size(diffexim_Q,2),M);
for i=1:S, mask_de (:,:,i) = diffexim_Q == i-1; end;
for j=1:M, mask_lpq(:,:,j) = lpqim_Q    == j-1; end;
for i=1:S,
    if any(any(mask_de(:,:,i))),
        for j=1:M,
            H2(i,j) = sum(sum(  mask_de(:,:,i) & mask_lpq(:,:,j)  ));
        end;
    end;
end;
% [i1,j1] = ndgrid(1:size(H2,1),1:size(H2,2));
% [i2,j2] = ndgrid(1:size(col,1),(1:size(col,2))+size(H2,2));
% H3 = accumarray([i1(:),j1(:);i2(:),j2(:)],[H2(:);col(:)]);
%H=horzcat(H2,col);
% vectorization
H = H2.';
H = H(:).';

